package assessment2;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a=2345;
         System.out.println("addition:"+(a+=8));
         System.out.println("divide:"+(a/=3));
         System.out.println("addition:"+(a%=5));
         System.out.println("addition:"+(a*=5));
	}

}
